$(function () {
  var e = window.location.pathname.replace("/", "");
  $(document).ready(function () {
    $(".navbar-nav")
      .find('a[href="' + e + '"]')
      .closest("li")
      .addClass("menu-active"),
      $(".dropdown-item")
        .find('a[href="' + e + '"]')
        .closest("li")
        .addClass("menu-active")
        .closest("ul")
        .closest("li")
        .addClass("menu-active");
  });
}),
  $(document).ready(function () {
    if ($(".uvp-xchcek-typed").length)
      new Typed(".uvp-xchcek-typed", {
        strings: ["Rápido", "Fácil", "En blockchain"],
        typeSpeed: 100,
        startDelay: 100,
        loop: !0,
      });
  }),
  $(document).ready(function () {
    $(".collapse")
      .on("shown.bs.collapse", function () {
        $(this)
          .parent()
          .find(".icon-change")
          .find("img")
          .attr("src", "assets/img/icons/ic_minus.svg");
      })
      .on("hidden.bs.collapse", function () {
        $(this)
          .parent()
          .find(".icon-change")
          .find("img")
          .attr("src", "assets/img/icons/ic_plus.svg");
      });
  }),
  $(document).ready(function () {
    $("a.a-hero-product").on("click", function (e) {
      e.preventDefault(),
        $("html, body").animate(
          { scrollTop: $("#feature-teamwork").offset().top },
          500
        );
    });
  }),
  (window.onscroll = function () {
    addShadow();
  });
var header = document.getElementById("js-header"),
  headerSection = document.getElementById("js-header-section"),
  logo = document.getElementById("logo-img"),
  sticky = header.offsetTop;
function addShadow() {
  window.pageYOffset > sticky
    ? (header.classList.remove("js-header-shadow-off"),
      headerSection.classList.remove("g-py-10"),
      header.classList.add("js-header-shadow-on"),
      logo.classList.add("logo-img-size"))
    : (header.classList.remove("js-header-shadow-on"),
      logo.classList.remove("logo-img-size"),
      header.classList.add("js-header-shadow-off"),
      headerSection.classList.add("g-py-10"));
}
$(window).width() < 993 &&
  ($("#nav-link--home").click(function () {
    $(".hs-sub-menu").toggle(),
      $("i", this).toggleClass("fa-angle-down fa-angle-up");
  }),
  $(".js-navigation").click(function (e) {
    !$(".u-main-nav-v5").is(e.target) &&
      0 === $(".u-main-nav-v5").has(e.target).length &&
      $(".hs-has-sub-menu").hasClass("hs-sub-menu-opened") &&
      ($("i", this).toggleClass("fa-angle-down fa-angle-up"),
      $(".hs-sub-menu").hide());
  }));
